import React, { Component } from 'react';


const emailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
const formValid = formErrors =>
{
    let valid =true;
    Object.values(formErrors).forEach(
        (val)=> 
        val.length > 0 && (valid=false)
    );
    return valid;
}

export class Contact extends Component {
    constructor(props)
    {
        super(props);
        this.state={
        name: null,
            
            email:null,
            subject:null,
            message:null,
            formErrors:{
                name :"",
                
                email:"",
                subkect:"",
                message:""
            }
        };

     }

     handleChange = e =>
   {
    e.preventDefault();
    const{name, value}= e.target;
    let formErrors=this.state.formErrors;
   
    switch(name)
    {
        case 'name' :
            formErrors.name = value.length < 3?"min 3 characters required"
            :"";
            break;

               

                case 'email' :
                    formErrors.email=
                    emailRegex.test(value)  ?""
                    :"Invalid email";
                    break;


                    case 'subject' :
                        formErrors.subject = value.length < 10 ?"min 10 characters required"
                        :"";
                        break;

                        case 'message' :
                            formErrors.message = value.length < 50 ?"min 50 characters required"
                            :"";
                            break;

                default: break;

    }
    this.setState({formErrors, [name]: value}); 
}




       handleSubmit = event =>
       {
           event.preventDefault();
           if(formValid(this.state.formErrors))
           {
               console.log(`
               ----SUBMITTING--
               Name: ${this.state.name}
               
               Email: ${this.state.email}
               Subject: ${this.state.subject}
               Message: ${this.state.message}
               `)
           }
           else{
               console.error('Invalid form')
           }
       }
    render() {
        const {formErrors}= this.state;
        return (
            <div className="wrapper">
                <div className="form-wrapper">
                   <h1>Query Form</h1>
                   <form onSubmit={this.handleSubmit} noValidate>
                       <div className="name">
                           <label htmlFor="name">Name</label>
                           <input type="text"

                            placeholder="Name" 
                            name="name" 
                            noValidate
                            onChange={this.handleChange}
                            />

                            {formErrors.name.length>0  && 
                                <span className="errorMessage">{formErrors.name}</span>
                            }
                       </div>

                     
                          

                       <div className="email">
                           <label htmlFor="email">Email</label>
                           <input type="email"
                           
                            placeholder="Email" 
                            noValidate
                            name="email" 
                            onChange={this.handleChange}
                            />

                           {formErrors.email.length>0  && (
                                <span className="errorMessage">{formErrors.email}</span>
                            )}
                       </div>

                       <div className="subject">
                           <label htmlFor="subject">Subject</label>
                           <input type="text"
                          
                            placeholder="Subject"
                            noValidate 
                            name="subject" 
                            onChange={this.handleChange}
                            />

                            {formErrors.subject.length>0  && 
                                <span className="errorMessage">{formErrors.subject}</span>
                            }
                       </div>

                       <div className="createAccount" >
                           <button className="submitbtn" type="submit">Create Account</button>
                           <small className="hlink">Already have an Account?</small>

                       </div>
                       
                   </form> 
                </div>

                
            </div>
        )
                        }
}

export default Contact
