import React, { Suspense } from 'react';
import 'antd/dist/antd.css';
import './Layout.css';
import { Layout, Menu } from 'antd';
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  StockOutlined,
} from '@ant-design/icons';
import { fetchSources } from '../API';

const NewsSection = React.lazy(() => import('./NewsSection'));
const News = React.lazy(() => import('./News'));

const { Header, Sider, Content } = Layout;

class MainLayout extends React.Component {
  state = {
    collapsed: false,
    sources: [],
    homePage: true,
    query: '',
    };

  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed,
    });
  };

  async componentDidMount() {
    const sources = await fetchSources();

    this.setState (Array.isArray(this.state.sources) );
  }

  // componentWillMount() {
  //   // Make HTTP reques with Axios
  //   axios.get(this.apiUrl).then(res => {
  //     // Set state with result
  //     this.setState({ data: res.data.sources });
  //     this.setState({ count: res.data.sources.length });
  //     //console.log(this.state.data);
  //     console.log(this.state.value);
  //     console.log(res.data);
  //   });
  // }

  loadNews(query) {
    this.setState({
      homePage: false,
      query: query
    });
  }

  render() {
    return (
      <Layout>
        <Sider trigger={null} collapsible collapsed={!this.state.collapsed}>
          <div className="logo"><h2>{!this.state.collapsed ? 'N' : 'Newsio'}</h2></div>
          <Menu theme="dark" mode="inline" defaultSelectedKeys={['-1']}>
            <Menu.Item onClick={() => this.setState({homePage: true })} key="-1" icon={<StockOutlined />}>
              Top News
            </Menu.Item>
            {this.state.sources.map((source)=>
              <Menu.Item onClick={() => this.loadNews(source.name)} key={source.name}>
                {source.name}
              </Menu.Item>
            )}
          </Menu>
        </Sider>
        <Layout className="site-layout">
          <Header className="site-layout-background" style={{ padding: 0 }}>
            {React.createElement(!this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
              className: 'trigger',
              onClick: this.toggle,
            })}
          </Header>
          <Content
            className="site-layout-background"
            style={{
              margin: '24px 16px',
              padding: 24,
              paddingRight: 0,
              minHeight: 280,
            }}
          >
            <Suspense fallback={<h1 style={{ fontSize: '30px' }}>Loading...</h1>}>
              {this.state.homePage === true ? <News /> : <NewsSection category='everything' query={'q=' + this.state.query} topHeading={this.state.query} results='100' />}
            </Suspense>
          </Content>
        </Layout>
      </Layout>
    );
  }
}

export default MainLayout;
