import './App.css';
import logo from './images/logo.png';
import slider1 from './images/news_slider_1.jpg';
import slider2 from './images/news_slider_2.jpg';
import slider3 from './images/news_slider_3.jpg';
import holder from './images/holder.jpg';

import Home from './Home.js';
import Business from './Business.js';
import Sports from './Sports.js';
import Entertainment from './Entertainment.js';
import Education from './Education.js';
import Health from './Health.js';
import Registration from './Registration.js';
import Contact from './Contact.js';
import Error from './Error.js';
import {Switch,Route} from 'react-router-dom';
import Contact_Us from './Contact Us';

import  {Link} from 'react-router-dom';

 import { DropdownSubmenu, NavDropdownMenu} from "react-bootstrap-submenu";
// // import {NavBar, Nav} from 'react-bootstrap';
 import { Navbar, NavItem, NavDropdown, MenuItem, Nav } from 'react-bootstrap';
  import Figure from 'react-bootstrap/Figure' ;
 import FigureImage from 'react-bootstrap/FigureImage';
import FooterM from './FooterM';
//import Carousel from 'react-bootstrap/Carousel'


function App() {



  return (
    <>
    <div className="container-fluid">
      <div className="jumbotron">
      <nav className="navbar navbar-light bg-light">
        <a href="#" className="navbar-brand" >
          <img src={logo} alt="News logo" width="80" height="80"/>
            <span>News Hub</span>
        </a>



      {/* <a href="https://www.facebook.com/" className="nav-item nav-link yt"><i className="fa fa-2x fa-facebook"></i></a>
			<a href="https://twitter.com/?lang=en" className="nav-item nav-link yt"><i className="fa fa-2x fa-twitter "></i></a>
			<a href="https://www.instagram.com/" className="nav-item nav-link yt"><i className="fa fa-2x fa-instagram "></i></a>
			<a href="https://www.youtube.com/" className="nav-item nav-link yt"><i className="fa fa-2x fa-youtube "></i></a>   */}


       <button type="button" className="btn btn-info float-right" data-toggle="popover" title="Popover title" data-content="And here's some amazing content. It's very engaging. Right?">SignUp/Login</button> 



      <marquee><strong>BREAKING NEWS:US passes 20 million novel coronavirus cases    
PM Modi to lay foundation stone of permanent campus of IIM-Sambalpur today</strong></marquee>
      </nav>

        </div>  

      {/* NAVIGATION    */}

      <div>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark sticky-top">

        {/* Creating button so that when reduce screen size there will be button in which all menu are will be there */}
    {/* 
  navbar-toggler is used for collapsing menu items
  data-toggler is used to hide section 
  data-target is used as id and same hide should be there in elemnt to hide items */}

            <button type="button" className="navbar-toggler" data-toggle="collapse" data-target="#menu">
              <span className="navbar-toggler-icon"></span>
            </button>

            <div className="collapse navbar-collapse" id="menu">
            <ul className="nav navbar-nav">


              {/* drop down with submenu */}
               <li className="nav-item dropdown">
                <a href="https://www.google.com/"  className="nav-link dropdown-toggle" data-toggle="dropdown">
                  Explore
                </a>

                <ul className="dropdown-menu">
                  <li><a href="https://www.google.com/" className="dropdown-item">INDIA</a></li>
                  <li><a href="https://www.google.com/" className="dropdown-item">WORLD</a></li>
                  {/* <li><a href="https://www.google.com/" className="dropdown-item">Health</a></li>

                  <li className="dropdown-submenu">
                    <a href="https://www.google.com/" className="dropdown-toggle" data-toggle="dropdown">
                      <span className="nav-label">Contact Us</span>
                    </a>
                    <li class="dropdown-submenu">
            <a class="test" href="#">Another dropdown <span class="caret"></span></a>
            <ul class="dropdown-menu">
              <li><a href="#">3rd level dropdown</a></li>
              <li><a href="#">3rd level dropdown</a></li>
            </ul>
          </li>
                  </li>  */}
                </ul>

               </li> 






              <li className="nav-item"><Link to="/" className="nav-link active">Home</Link> </li>
              <li className="nav-item"><Link to="/business" className="nav-link">Business</Link> </li>
              <li className="nav-item"><Link to="/sports" className="nav-link">Sports</Link> </li>
              <li className="nav-item"><Link to="/entertainment" className="nav-link">Entertainment</Link> </li>
              <li className="nav-item"><Link to="/science" className="nav-link">Science</Link> </li>
              <li className="nav-item"><Link to="/health" className="nav-link">Health</Link> </li>
              {/* <li className="nav-item"><Link to="/registration" className="nav-link">Registration</Link> </li> */}
              <li className="nav-item"><Link to="/Contact Us" className="nav-link">Contact Us</Link> </li>

            </ul>

            {/* Searh Bar */}
              <form className="form-inline  ml-5 my-2 my-lg-0">
                <input className="form-control mr-sm-2" type="search" placeholder="Search"></input>
                <button className="btn btn-outline-primary my-2 my-sm-0 "  type="submit">Search</button>
              </form>
            </div>  

        </nav>
      </div>
{/* 
Content Part */}

    <Switch>
      <Route exact path='/' component={Home}/>
      <Route exact path='/business' component={Business}/>
      <Route exact path='/sports' component={Sports}/>
      <Route exact path='/entertainment' component={Entertainment}/>
      <Route exact path='/education' component={Education}/>
      <Route exact path='/health' component={Health}/>
      <Route exact path='/registration' component={Registration}/>
      <Route exact path="/Contact Us" component={Contact_Us} />
      <Route component={Error}/>
    </Switch>

    {/* <div className="title">
            <h1>&lt;insert fancy name&gt;</h1>
            <h4>
              Powered by
              <a href="https://newsapi.org/"> News API </a>ðŸ”¥{' '}
            </h4>
          </div>*/}

    <FooterM/>
    <br/><br/>

    </div>
    </>
  );
}

export default App;
