import React , {useState, useEffect, createRef } from 'react';
import { Card, CardActionArea, CardActions, CardContent, CardMedia, Button, Typography,} from '@material-ui/core';

import classNames from 'classnames';
import useStyles from './styles.js';


const NewsCard = ({article :{description, publishAt, source, url, title, urlToImage}, i, activeArticle}) => {
    const classes= useStyles();
    const [elRefs, setElRefs] = useState([]);

    useEffect(() =>
    {
        setElRefs((refs)=> Array(2).fill().map((_, j)=>refs[j] || createRef()));
    }, [])


    return (
        <Card className={classNames(classes.card, activeArticle ===i ? classes.activeCard :null)}>
           <CardActionArea href={url} target="_blank">
               <CardMedia className={classes.media} image={urlToImage || 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.freepik.com%2Fpremium-vector%2Fheadline-news-coronavirus-banner-background-template_7516542.htm&psig=AOvVaw3iBjFccho_4Gbc8Tn2ZSk2&ust=1610217514566000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCLiI3-n9jO4CFQAAAAAdAAAAABAS'} />
            <div className={classes.details}>
                <Typography variant="body2" color="textSecondary" component="h2" >{(new Date(publishAt)).toDateString()}</Typography>
                <Typography variant="body2" color="textSecondary" component="h2" >{source.name}</Typography>
            </div>
            <Typography  classame={classes.title} gutterBottom variant="h5" >{title}</Typography>

            <CardContent>
            <Typography variant="body2" color="textSecondary" component="p" >{description}</Typography> 
            </CardContent>
           </CardActionArea>

           <CardActions className={classes.CardActions}>
               <Button size="small" color="primary">Learn More</Button>
               <Typography variant="h5" color="textSecondary">{i+1}</Typography>
           </CardActions>
        </Card>
    )
}

export default NewsCard;
