import React, { Component } from 'react'

export class Contact_Us extends Component {
   
    constructor(props) {
        super(props);
        this.state = {
          firstname: '',
          lastname:'',
          email: '',
          
          message: ''
        }
    }

    submitEmail(e){
        e.preventDefault();
        axios({
          method: "POST", 
          url:"/send", 
          data:  this.state
        }).then((response)=>{
          if (response.data.status === 'success'){
              alert("Message Sent."); 
              this.resetForm()
          }else if(response.data.status === 'fail'){
              alert("Message failed to send.")
          }
        })
}

resetForm()
{
        this.setState({firstname: '', lastname:'',email: '', message: ''})
}

    onFirstNameChange(event) {
        this.setState({firstname: event.target.value})
    }

    onLastNameChange(event) {
        this.setState({lastname: event.target.value})
    }

    onEmailChange(event) {
        this.setState({email: event.target.value})
    }

    onSubjectChange(event) {
        this.setState({message: event.target.value})
    }

    
   



    render() {
        return (
            <div>
            <section class="Material-contact-section section-padding section-dark">
            <div className="jumbotron-fluid" >
            <div class="container" className="A">
            </div>
            </div>

        
           <div class="container">
           <div class="row">
               <div class="col-md-6 mt-5 contact-widget-section2 wow animated fadeInRight" data-wow-delay=".2s">
                  
                  <div className="container-sm  pt-3" style={{maxWidth:'500px'}}>
                    {/* <h5 class="card-header" style={{ textAlign: 'center', backgroundColor: 'white' }}>Love to Hear From You</h5> */}
                    <div class="card-body" >
                    <form id="contact-form" onSubmit={this.submitEmail.bind(this)} 
                                    method="POST" >
                        <h3 style={{color:"blue"}}><b>Contact Us</b></h3>

                        <div className="form-group">
                            <label>First name</label>
                            <input type="text" className="form-control" id="firstname" placeholder="First name" required value={this.state.firstname} 
                                       onChange={this.onFisrtNameChange.bind(this)} />
                        </div>

                        <div className="form-group">
                            <label>Last name</label>
                            <input type="text"  id="lastname"  className="form-control" name="lastName" placeholder="Last name" required value={this.state.lastname} 
                                       onChange={this.onLastNameChange.bind(this)}/>
                        </div>

                        <div className="form-group">
                            <label>Email address</label>
                            <input type="email" id="email" className="form-control" name="email" placeholder="Enter email" required value={this.state.email} 
                            onChange={this.onEmailChange.bind(this)}/> 
                        </div>

                        <div className="form-group">
                            <label>Your message here</label>
                            <textarea rows="4" id="message" className="form-control" name="message" placeholder="Write us" required value={this.state.subject}
                                      onChange={this.onSubjectChange.bind(this)} />
                        </div>

                {/* <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" required />
                </div> */}

                        {/* <button type="submit" className="btn btn-primary btn-block">Get Callback</button> */}
                        <Button variant="contained" color="primary" onClick={formSubmit}>{data.buttonText}</Button>
                {/* <p className="forgot-password text-center">
                    Already registered <a href="#">sign in?</a>
                </p> */}
                    </form>
                </div>
           </div>
           </div>

                <div class="col-md-6 mt-5 contact-widget-section2 wow animated fadeInLeft" data-wow-delay=".2s">
                <iframe src="https://maps.google.com/maps?q=warsaw&t=k&z=13&ie=UTF8&iwloc=&output=embed" width="100%" height={538} frameBorder={0} style={{border: 0}} allowFullScreen data-aos="fade-left" data-aos-duration={3000} />
                </div>


            </div>
        </div>

        <section>
           <div>
            <ContactFooter/>
            </div>
          </section>
       
       
      </section>
     
        </div>
        )
    }
}

export default Contact_Us
