import React, { Component } from 'react'
import './SignUpForm.css'


const emailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
const formValid = formErrors =>
{
    let valid =true;
    Object.values(formErrors).forEach(
        (val)=> 
        val.length > 0 && (valid=false)
    );
    return valid;
}


export class FormSignUpForm extends Component {
    constructor(props)
    {
        super(props);
        this.state={
            firstName: null,
            lastName: null,
            email:null,
            password:null,
            formErrors:{
                firstName :"",
                lastName:"",
                email:"",
                password:""
            }
        };

     }

     handleChange = e =>
   {
    e.preventDefault();
    const{name, value}= e.target;
    let formErrors=this.state.formErrors;
   
    switch(name)
    {
        case 'firstName' :
            formErrors.firstName = value.length < 3?"min 3 characters required"
            :"";
            break;

                case 'lastName' :
                    formErrors.lastName = value.length < 3 ?"min 3 characters required"
                    :"";
                    break;

                case 'email' :
                    formErrors.email=
                    emailRegex.test(value)  ?""
                    :"Invalid email";
                    break;


                    case 'password' :
                        formErrors.password = value.length < 3 ?"min 3 characters required"
                        :"";
                        break;

                default: break;

    }
    this.setState({formErrors, [name]: value}); 
}




       handleSubmit = event =>
       {
           event.preventDefault();
           if(formValid(this.state.formErrors))
           {
               console.log(`
               ----SUBMITTING--
               First Name: ${this.state.firstName}
               Last Name: ${this.state.lastName}
               Email: ${this.state.email}
               Password: ${this.state.password}

               `)
           }
           else{
               console.error('Invalid form')
           }
       }
    render() {
        const {formErrors}= this.state;
        return (
            <div className="wrapper">
                <div className="form-wrapper">
                   <h1>Create Account</h1>
                   <form onSubmit={this.handleSubmit} noValidate>
                       <div className="firstName">
                           <label htmlFor="FirstName">First Name</label>
                           <input type="text"

                            placeholder="First Name" 
                            name="firstName" 
                            noValidate
                            onChange={this.handleChange}
                            />

                            {formErrors.firstName.length>0  && 
                                <span className="errorMessage">{formErrors.firstName}</span>
                            }
                       </div>

                       <div className="lastName">
                           <label htmlFor="lastName">Last Name</label>
                           <input type="text"
                          
                            placeholder="Last Name" 
                            name="lastName"
                            noValidate 
                            onChange={this.handleChange}
                            />

                       {formErrors.lastName.length>0  && 
                                <span className="errorMessage">{formErrors.lastName}</span>
                            }
                       </div>

                       <div className="email">
                           <label htmlFor="email">Email</label>
                           <input type="email"
                           
                            placeholder="Email" 
                            noValidate
                            name="email" 
                            onChange={this.handleChange}
                            />

                           {formErrors.email.length>0  && (
                                <span className="errorMessage">{formErrors.email}</span>
                            )}
                       </div>

                       <div className="password">
                           <label htmlFor="password">Password</label>
                           <input type="password"
                          
                            placeholder="Password"
                            noValidate 
                            name="password" 
                            onChange={this.handleChange}
                            />

                            {formErrors.password.length>0  && 
                                <span className="errorMessage">{formErrors.password}</span>
                            }
                       </div>

                       <div className="createAccount" >
                           <button className="submitbtn" type="submit">Create Account</button>
                           <small className="hlink">Already have an Account?</small>

                       </div>
                       
                   </form> 
                </div>

                
            </div>
        )
    }
}

export default FormSignUpForm
