import React, { Component } from 'react';
import axios from 'axios';
import DisplayEntertain from './DisplayEntertain.js';
import OutletEntertain from './OutletEntertain.js';
import '../Outlet.css';
import '../Search.css';
import '../Display.css';

const apiKey = '517e1eb7074b4b0f9543864552059bea';
class Entertain extends Component {

  constructor(props) {
    // Pass props to parent class
    super(props);
    // Set initial state
    this.state = {
      data: [],
      count: 0,
      // value:'bbc-news'
      value: this.props.default
    };
    this.handleChange = this.handleChange.bind(this);

    this.apiUrl = `https://newsapi.org/v2/sources?category=entertainment&apiKey=${apiKey}`;
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
    console.log(this.state.value);
  }

  // Lifecycle method
  componentWillMount() {
    // Make HTTP reques with Axios
    axios.get(this.apiUrl).then(res => {
      // Set state with result
      this.setState({ data: res.data.sources });
      this.setState({ count: res.data.sources.length });
      //console.log(this.state.data);
      console.log(this.state.value);
      console.log(res.data);
    });
  }

  render() {
    return(
     
      <div class="card-deck">
    {this.state.articles.map((news, i) => {
  return(
    <div class="col-md-4 card-deck mb-5 mx-auto">
      <div class="card  ml-5"  key={i} >
       <div class="row g-0">
        <div class="col-md-11 col-lg-11 col-sm-8 col-xs-8 mx-auto">
        <img
        src={news.urlToImage}
        class="card-img-top img-fluid"
        alt="..."/>
         </div>
         <div class="col-md-11 col-lg-11 col-sm-8 col-xs-8 mx-auto">
      <div class="card-body text-dark bg-white">
        <h3 class="card-title "><a href={news.url} class="text-info" target="_blank" rel="noopener noreferrer">
                    {news.title}
                  </a></h3>
        <p class="card-text"><h5>{news.description}</h5>
        
        </p>
        <p class="text-success">
                    By <i>{news.author ? news.author : this.props.default}</i>
                    </p>
                  <p> <b><large class="text-danger">{this.formatDate(news.publishedAt)}</large></b></p>
                  
      </div>
     
      {/* <div class="card-footer bg-warning">
      <small class="text-primary">{this.formatDate(news.publishedAt)}</small>
    </div> */}
      </div>
      
      </div>
      </div>
      </div>
    
    );
    
    })}
     </div>
    );
  }
}

export default Entertain;
