import React, { Component } from 'react';
import ContactFooter from './ContactFooter.js';
import axios from 'axios';
 
export class Contact_Us extends Component {
   
    state={
        email:'',
        email1:'',
        email2:''
        
    }

    //handle input
handleEmail=(e)=>{
    this.setState({
        email:e.target.value
    })
}

formSubmit=(e)=>{
    e.preventDefault();
    let data={
        email:this.state.email,
    
    }
    axios.post('/api/forma',data)
    .then(res=>{
        this.setState({
            
            sent:true,
    
        }.this.resetform())
        }).catch(()=>{
            console.log('mesaage not sent');
        })
    
    }
    
    resetForm=()=>{
        this.setState({
            email:''
        })
    
        setTimeout(()=>{
            this.setState({
                sent:false
            })
        },3000)
    }
  
render() {
        return (
            <section class="Material-contact-section section-padding section-dark">
            <div class="container">
               
                <div class="row">
                 
                    
                   
                    <div class="col-md-6 mt-5 contact-widget-section2 wow animated fadeInRight" data-wow-delay=".2s">
    
    {/* <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#eee' }}> */}
    <div className="container-sm  pt-3" style={{maxWidth:'500px'}}>
                        {/* <h5 class="card-header" style={{ textAlign: 'center', backgroundColor: 'white' }}>Love to Hear From You</h5> */}
                        <div class="card-body" >
                        <form >
                    <h3 style={{color:"blue"}}><b>Contact Us</b></h3>
    
                    <div className="form-group">
                        <label>First name</label>
                        <input type="text" className="form-control" name="firstname" placeholder="First name" required />
                    </div>
    
                    <div className="form-group">
                        <label>Last name</label>
                        <input type="text" className="form-control" name="lasttname" placeholder="Last name" required/>
                    </div>
    
                    <div className="form-group">
                        <label>Email address</label>
                        <input type="email" className="form-control" name="email" placeholder="Enter email" required />
                    </div>
    
                    <div className="form-group">
                        <label>Your message here</label>
                        <textarea rows="4" className="form-control" name="message" placeholder="Write us" required />
                    </div>
    
                    {/* <div className="form-group">
                        <label>Password</label>
                        <input type="password" className="form-control" placeholder="Enter password" required />
                    </div> */}
    
                    <button type="submit" className="btn btn-primary btn-block">Get Callback</button>
                    {/* <p className="forgot-password text-center">
                        Already registered <a href="#">sign in?</a>
                    </p> */}
                </form>
    
                            
                        </div>
               
                </div>
    
                    </div>
    
                    <div class="col-md-6 mt-5 contact-widget-section2 wow animated fadeInLeft" data-wow-delay=".2s">
                    <iframe src="https://maps.google.com/maps?q=warsaw&t=k&z=13&ie=UTF8&iwloc=&output=embed" width="100%" height={538} frameBorder={0} style={{border: 0}} allowFullScreen data-aos="fade-left" data-aos-duration={3000} />
                    </div>
    
    
                </div>
            </div>
    
            <section>
               <div>
                <ContactFooter/>
                </div>
              </section>
           
           
          </section>
        
    
   
        
        )
    }
}

export default Contact_Us
