import React, { Component } from 'react'
import firebase from './firebase';

const phone='';
export class OTPFirebase extends Component {
   
  setUpRecaptcha=()=>{
    window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('recaptcha-container', {
      'size': 'normal',
      'callback': function(response)  {
        // reCAPTCHA solved, allow signInWithPhoneNumber.
        this.onSignInSubmit();
      },
    });
    
  };

  onSignInSubmit=(event)=>{
    // getPhoneNumberFromUserInput()
    //event.preventDefault();
     this.setUpRecaptcha();
    const phoneNumber =  this.state.mobileno;
console.log(phoneNumber);
const appVerifier = window.recaptchaVerifier;
firebase.auth().signInWithPhoneNumber(phoneNumber, appVerifier)
    .then((confirmationResult) => {
      // SMS sent. Prompt user to type the code from the message, then sign the
      // user in with confirmationResult.confirm(code).
      window.confirmationResult = confirmationResult;

      const code = window.prompt("Enter the OTP");
confirmationResult.confirm(code).then((result) => {
  // User signed in successfully.
  const user = result.user;

  console.log("User is signed in")
  // ...
}).catch((error) => {
  // User couldn't sign in (bad verification code?)
  // ...
});
      // ...
    }).catch((error) => {
      // Error; SMS not sent
      // ...
    });

  }

  onChangeInput =(event)=>
  {
    this.mobileno=event.target.value;
  }
  render() {
    
    return (
        
      <div className="App">
      <div className="container">
      <div className="row justify-content-center align-items-center">
        <div className="col-sm-6 text-left">
          <h2>Phone Login</h2>
          <form onSubmit={this.onSignInSubmit(this.state.mobileno)}>
              <div id="recaptcha-container"></div>

            <div className="form-group">
              <label htmlFor="exampleInputEmail1">Phone Number</label>
              <input type="number" name="mobileno" onChange={(e)=>this.onChangeInput(e)} required className="form-control"/>

            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>
        </div>
    </div>
    </div>
    </div>
    )
  }
}





export default OTPFirebase
